package com.example.examen_t3;

import android.os.Bundle;
import android.util.Log;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.examen_t3.adapter.PokemonAdapter;
import com.example.examen_t3.entities.Pokemon;
import com.google.gson.Gson;

import java.util.Arrays;
import java.util.List;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;
import retrofit2.Retrofit;
import retrofit2.converter.gson.GsonConverterFactory;

public class Pokemons extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_pokemons);

        RecyclerView rv = findViewById(R.id.listasPokemons);
        rv.setHasFixedSize(true);
        rv.setLayoutManager(new LinearLayoutManager(getApplicationContext()));

        Retrofit retrofit = new Retrofit.Builder()
                .baseUrl("https://628f68e4dc478523653ffd8a.mockapi.io/api/pokemon")
                .addConverterFactory(GsonConverterFactory.create())
                .build();

        PokemonService service = retrofit.create(PokemonService.class);



        service.allPokemon().enqueue(new Callback<List<Pokemon>>() {
            @Override
            public void onResponse(Call<List<Pokemon>> call, Response<List<Pokemon>> response) {
                Log.i("MAIN_APP", String.valueOf(response.code()));
                Log.i("MAIN_APP", new Gson().toJson(response.body()));

                if(response.code() == 200) {
                    PokemonAdapter adapter = new PokemonAdapter(response.body());
                    rv.setAdapter(adapter);
                }
            }

            @Override
            public void onFailure(Call<List<Pokemon>> call, Throwable t) {
                Log.i("AIN_APP", "No hubo comunicacion con el servidor");
            }
        });

        List<Pokemon> datos = Arrays.asList(new Pokemon("Squartl", "Agua",
                "data:image/jpeg;base64,", -7.1570273f,  78.517594f));

        PokemonAdapter adapter = new PokemonAdapter(datos);
        rv.setAdapter(adapter);

    }


}